package com.tss.basics.debugging;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double var = 10;
		System.out.println(var);
		
		int var2 = (int) 10.5;
		System.out.println(var2);
		
		byte a = 12;
		byte b = (byte)(a+20);
		System.out.println(b);
	}

}
